mapper.select:
select hcode, htype, rtype, hname, area, flr, hetc, deposit, mrent, yrent, maintc
from house_info join house_price using(hcode)
where addr like '%'||#{search}||'%' or hname like '%'||#{search}||'%'

select count(*)
from house_info join house_price using(hcode)
where addr like '%'||#{search}||'%' or hname like '%'||#{search}||'%'

select hcode, htype, rtype, hname, area, flr, hetc, deposit, mrent, yrent, maintc
from house_info join house_price using(hcode)
where registerdate > sysdate-3

